mapper.select:
SELECT DBO.UFGEN_INSP_RST_NO('IR', 3), -- 점검결과 번호 MFC.FACILITY_NM, -- 설비명 MFI.INSPECT_NM, -- 점검명 CD1.CODE_NM AS RESULT, -- 점검결과 USR.USER_NM, -- 요청자 CIR.FACILITY_NO, -- 설비번호 CIR.INSP_RST_NO, -- 점검결과번호 EP1.EMPLOYEE_NM -- 접수자 FROM CMM_INSPECT_RST AS CIR -- 설비점검결과 LEFT OUTER JOIN SYS_USER AS USR WITH(NOLOCK) ON (CIR.REG_ID= USR.USER_ID) LEFT OUTER JOIN MDM_FACILITY AS MFC WITH(NOLOCK) ON (CIR.FACILITY_NO = MFC.FACILITY_NO) -- 설비정보 LEFT OUTER JOIN MDM_FACILITY_INSP AS MFI WITH(NOLOCK) ON (CIR.INSPECT_ID = MFI.INSPECT_ID) -- 설비점검기준정보 LEFT OUTER JOIN MDM_EMPLOYEE AS EP1 WITH(NOLOCK) ON (CIR.REG_ID = EP1.EMPLOYEE_ID) LEFT OUTER JOIN MDM_CODE AS CD1 WITH(NOLOCK) ON (CIR.RESULT_CD = CD1.CODE)
WHERE MFC.FACILITY_NO = #{facility_no}
ORDER BY DBO.UFGEN_INSP_RST_NO('IR', 3)
